mapper.select:
select

from sys_user


		AND login_name = #{loginName}


		AND id = #{id}

select
a.*
from sys_user a

	, sys_usergroup_user b


	, sys_user_role c



		AND a.id = b.user_id AND b.group_id= #{userGroupId}


		AND a.id=c.user_id AND c.role_id= #{roleId}


		 AND a.status = 'available' 


		AND login_name = #{loginName}
