mapper.insert:

insert into sys_resource (id, code, name,
type, system_type, icon,
priority, url, parent_code,
parent_codes, permission, available
)
values (#{id,jdbcType=CHAR}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR},
#{type,jdbcType=VARCHAR}, #{systemType,jdbcType=BIT}, #{icon,jdbcType=VARCHAR},
#{priority,jdbcType=INTEGER}, #{url,jdbcType=VARCHAR}, #{parentCode,jdbcType=VARCHAR},
#{parentCodes,jdbcType=VARCHAR}, #{permission,jdbcType=VARCHAR}, #{available,jdbcType=BIT}
)

mapper.select:
select

permission, available
from sys_resource


		AND name like concat('%', #{name} ,'%')


		AND type = #{type}


		AND parent_code = #{parentCode}


		AND system_type = #{systemType}


		AND available = #{available}

SELECT max(code) as code FROM sys_resource
where
parent_code=#{parentCode}

select

FROM sys_resource
WHERE id in(SELECT resource_id FROM sys_user_resource WHERE user_id=#{userId});

select
t.id,t.`code`,t.`name`,t.type,t.system_type,t.icon,t.priority,t.url,t.parent_code,
t.parent_codes,t.permission,t.available
from sys_resource t


		AND t.system_type = #{systemType}


		AND t.available = #{available}
		AND
		(select COUNT(*) from sys_resource s where FIND_IN_SET(s.`code`,REPLACE(t.parent_codes,'/',',')) and s.available = #{systemType} ) = #{available}


ORDER BY code ASC

select

from sys_resource a
where 1=1
AND a.system_type = #{systemType}
AND a.available = #{available}

select
from sys_resource a where 1=1 AND a.code NOT IN
#{id}
