mapper.select:
select

from sys_resource


		AND name like concat('%', #{name} ,'%')


		AND type = #{type}


		AND parent_code = #{parentCode}


		AND system_type = #{systemType}


		AND available = #{available}


		AND is_leaf = #{isLeaf}

mapper.update:
update sys_resource
set available=#{available}
where id = #{id}

mapper.select:
select a.* from
sys_resource a

	,sys_role_resource b


	,sys_user_resource c



		AND a.available = #{available}


		AND a.system_type = #{systemType}


		AND a.id=b.resource_id AND
		b.role_Id=#{roleId}


		AND a.id=c.resource_id AND
		c.user_Id=#{userId}


		AND is_leaf = #{isLeaf}

SELECT max(code) as code FROM sys_resource
where
parent_code=#{parentCode}

select

from sys_resource a
where 1=1
AND a.system_type = #{systemType}
AND
a.available = #{available}

select
from sys_resource a where 1=1 AND a.code NOT IN
#{id}
